def add_offramps(path_dict):
	new_path_dict = {}

	for key in path_dict.keys():
		end_point = key[1]
		new_path_dict[key] = path_dict[key] + ramps[end_point]

		if len(path_dict[key]) > 0:
			dist = dist_between(path_dict[key][-1], ramps[end_point][0])
			if dist > .51:
				print(key)
				print(dist)


	return new_path_dict


